import React, { useEffect, useState } from 'react'
import './Feed.css'
import InputOption from './InputOption'
import CreateIcon from '@material-ui/icons/Create';
import ImageIcon from '@material-ui/icons/Image';
import SubscriptionsIcon from '@material-ui/icons/Subscriptions';
import EventNoteIcon from '@material-ui/icons/EventNote';
import CalendarViewDayIcon from '@material-ui/icons/CalendarViewDay';
import Post from './Post';
// import db from local db
import { db } from './Firebase'
import Firebase from './Firebase'

// useState = Hook // RE-EXPLAIN 2:20
function Feed() {

 const [input, setInput] = useState('');
 const [posts, setPosts] = useState([]);

 //! useEffect hook allows us to fire off code when the Feed component loads. 
 //! It also allows us to fire off whatever the component re-renders if we don't pass a second argument.
 //! If we pass on a blank dependency like [empty array] in line 21, then it will fire off once the feed component loads but it will never fire off again after that 
 useEffect(() => {
    db.collection('posts').onSnapshot((snapshot) => 
        setPosts(
            snapshot.docs.map((doc) => ({
                id: doc.id,
                data: doc.data(),
            }))
        )
    );//real time posts collection when posts are add/deleted
}, []);


    // prevent refreshing
    const sendPost = e => {
        e.preventDefault();
        db.collection('posts').add({
            name: 'Ray Brown',
            description: 'test',
            message: input,
            photoURL: '',
            timestamp: Firebase.firestore.FieldValue.serverTimestamp()
        })
    };

    return <div className='feed'>
        <div className='feed_inputContainer'>
            <div className="feed_input">
                <CreateIcon />
                <form>
                    <input value={input} onChange={e => setInput(e.target.value)} type='text' />
                    <button onClick={sendPost} type='submit'>Send</button>
                </form>
            </div>
            <div className='feed_inputOptions'>
                <InputOption Icon={ImageIcon} title='Photo' color='#70B5F9' />
                <InputOption Icon={SubscriptionsIcon} title='Video' color='#E7A33E' />
                <InputOption Icon={EventNoteIcon} title='Photo' color='#70B5F9' />
                <InputOption Icon={ImageIcon} title='Event' color='#C0CBCD' />
                <InputOption 
                 Icon={CalendarViewDayIcon}
                 title='Write article'
                 color= '#7FC15E' />
            </div>
        </div>

        {/* every time I have a post I render it on the browser */}
        {posts.map(({id, data: {name, description, }}) => (
         <Post />
        ))}

        <Post 
        name='Ray Brown' 
        description='test' 
        message='wow it worked'
        />
        </div>
}

export default Feed
