import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { auth } from './Firebase';
import { login } from './features/userSlice'
import './Login.css'

function Login() {

    const [name, setName] = useState('');
    const [profilePic, setProfilePic] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const dispatch = useDispatch();
    // const [name, setName] = useState(''); for registering

    const loginToApp = (e) => {
        e.preventDefault();

    };
    const register = () => {
        if(!name){
            return alert("Please enter full name");
        }

        auth.createUserWithEmailAndPassword(email, password).then(
            (userAuth) => {
              userAuth.user
                .updateProfile({
                 displayName: name,
                 photoURL: profilePic,
              })
              .then(() => {
                dispatch(login({
                    email: userAuth.user.email,
                    uid: userAuth.user.uid,
                    displayName: name,
                    photoUrl: profilePic    
                }));
            });
        }).catch;
    };

    return (
    <div className='login'>
      <img src='https://news.hitb.org/sites/default/files/styles/large/public/field/image/500px-LinkedIn_Logo.svg_1.png?itok=q_lR0Vks' alt=''
        /> 

      <form>
          <input value={name} onChange={e => setName(e.target.value)} placeholder='Full Name' type='email'/>
          <input value={profilePic} onChange={e => setProfilePic(e.target.value)} placeholder='Profile pic URL (optional)' type='email'/>
          <input value={email} onChange={e => setEmail(e.target.value)} placeholder='Email' type='email'/>
          <input value={password} onChange={e => setPassword(e.target.value)} placeholder='Password' type='password'/>
          <button type='submit' onClick={loginToApp} >Sign-In</button>
      </form>

      <p>
          Not a member? {' '}
          <span className='login_register' onClick={register}>
            Create an Account</span>
      </p>
    </div>
    )
}

export default Login
